import { Link, useLocation } from 'react-router-dom';
import './Navbar.css';
import logo from "../Assets/logo-white.png";

const Navbar = () => {
  const location = useLocation();

  const showLoginButton = location.pathname !== "/login";
  const showSignUpButton = location.pathname !== "/signup";

  return (
    <nav>
      <Link to="/">
        <img src={logo} alt="Logo" className="logo" />
      </Link>
      <ul>
        {showLoginButton && (
          <li>
            <Link to="/login" className="button gray">Log in</Link>
          </li>
        )}
        {showSignUpButton && (
          <li>
            <Link to="/signup" className="button gray">Sign Up</Link>
          </li>
        )}
      </ul>
    </nav>
  );
}

export default Navbar;
