import React, { useState } from 'react';
import { useHistory } from 'react-router-dom'; // if you are using react-router for navigation
import "./Dashboard.css";
import Sidebar from "../Sidebar/Sidebar";
import MenuIcon from '@mui/icons-material/Menu';
import UserIcon from '@mui/icons-material/AccountCircle'; // Importing an icon for the user profile
import LogoImage from "../Assets/logo-white.png";

const Dashboard = () => {
  const [sidebarOpen, setSidebarOpen] = useState(false);
  const history = useHistory(); // For navigation

  const navigateToProfile = () => {
    // Navigate to the user profile page
    history.push('/user-profile');
  }

  return (
    <div className="Dashboard">
      <nav className="navbar">
        <button className="menu-btn" onClick={() => setSidebarOpen(!sidebarOpen)}>
          <MenuIcon />
        </button>
        <img src={LogoImage} alt="Logo" className="logo" />
        <button className="profile-btn" onClick={navigateToProfile}>
          <UserIcon />
        </button>
      </nav>
      <Sidebar isOpen={sidebarOpen} />
      <main className={`main-content ${sidebarOpen ? "shifted" : ""}`}>
        {/* Your main app content */}
      </main>
    </div>
  );
};

export default Dashboard;
